const intialState = {
  products: [],
};

const ProductReducer = (state = intialState, action) => {
  switch (action.type) {
    case "ADD_PRODUCT":
      return {
        products: state.products.concat([action.product]),
      };
    case "DELETE_PRODUCT":
      return {
        products: state.products.filter((p) => p.id !== action.id),
      };
    case "EDIT_PRODUCT":
      return {
        products: state.products.filter((p) => p.id === action.id),
      };
    case "UPDATE_PRODUCT":
      return {
        products1: state.products.filter((p) => p.id !== action.product.id),
        products: products1.concat([action.product]),
      };

    // let products = state.products.map((p) => {
