import React from "react";
import { connect } from "react-redux";

class ProductsList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {};
    this.deleteEmployee = this.deleteEmployee.bind(this);
  }
  deleteEmployee(id) {
    this.props.dispatch({
      type: "DELETE_PRODUCT",
      id: id,
    });
