const intialState = {
  products: [],
};

const ProductReducer = (state = intialState, action) => {
  switch (action.type) {
    case "ADD_PRODUCT":
      return {
        products: state.products.concat([action.product]),
      };
    case "DELETE_PRODUCT":
      return {
        products: state.products.filter((p) => p.id !== action.id),
      };

    default:
      return state;
  }
};
export default ProductReducer;
