import React from "react";
import { connect } from "react-redux";

class ProductsList extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      name: "",
      price: "",
    };
    this.deleteEmployee = this.deleteEmployee.bind(this);
    this.editEmployee = this.editEmployee.bind(this);
  }
  deleteEmployee(e) {
    this.props.dispatch({
      type: "DELETE_PRODUCT",
      id: e.target.id.value,
    });
    this.props.history.push("/");
  }
  editEmployee(e) {
    console.log("edit employee");
    // let product = {
    //   id: e.target.id.value,
    //   name: e.tareget.name.value,
    //   price: e.target.price.value,
    // };
    // this.props.dispatch({
    //   type: "EDIT_Product",
    //   product,
    // });
    this.props.history.push("/editproduct");
  }
  render() {
    return (
      <div>
        <h3> Products List</h3>
        <br />
        <br />
        <table border="1">
          <thead>
            <tr>
              <th>Id</th>
              <th>Name</th>
              <th>Price</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {this.props.products.map(
              (product, index) => (
                <tr>
                  <td>{product.id}</td>
                  <td>{product.name}</td>
                  <td>{product.price}</td>
                  <td>
                    <button
                      className="btn btn-success"
                      onClick={() => this.deleteEmployee(this)}
                    >
                      {" "}
                      Delete
                    </button>
                    <button
                      className="btn btn-success"
                      onClick={() => this.editEmployee(this)}
                    >
                      {" "}
                      Edit
                    </button>
                  </td>
                </tr>
              ),
              this
            )}
            ,
          </tbody>
        </table>
      </div>
    );
  }
}
function mapStateToProps(state) {
  return {
    products: state.products,
  };
}
export default connect(mapStateToProps)(ProductsList);
