const intialState = {
  products: [],
};

const ProductReducer = (state = intialState, action) => {
  switch (action.type) {
    case "ADD_PRODUCT":
      return {
        products: state.products.concat([action.product]),
      };

    default:
      return state;
  }
};
function mapStateToProps(state) {
  return {
    products: state.products,
  };
}
export default ProductReducer;
