import React, { Component } from "react";
import { connect } from "react-redux";
class AddProdct extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      name: "",
      price: "",
    };
  }
  handleChange(e) {
    [e.target.name] = e.target.value;
  }
  handleSubmit = (e) => {
    e.preventDefault();
    this.setState((e.target.name = e.target.value));
  };
  render() {
    return (
      <div>
        <h3> Add Product</h3>
        <br />
        <br />
        <form onSubmit={this.handleSubmit}>
          <label>Enter Id</label>:<span />
          <input
            type="text"
            name="id"
            placeholder="Id"
            value={this.state.id}
            onChange={this.handleChange}
          ></input>
        </form>
      </div>
    );
  }
}
export default connect()(AddProdct);
